mapper.select:
select

  distinct


from customer_coupon




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}

SELECT coupon_code AS couponCode, COUNT(1) AS number FROM customer_coupon
coupon_code IN
#{couponCode}
AND use_status = #{useStatus}
GROUP BY coupon_code

select  cp.id, cp.coupon_code, cp.customer_id, cp.receive_type, cp.send_user, cp.create_time, cp.use_start_time,
cp.use_end_time, cp.use_time, cp.use_status, cp.code
from customer_coupon as cp
where   coupon_code = #{couponCode,jdbcType=VARCHAR}

  and receive_type = #{receiveType,jdbcType=TINYINT}


  and  cp.customer_id IN

    #{id}


order by cp.create_time desc

select cp.id, cp.coupon_code, cp.customer_id, cp.receive_type, cp.send_user, cp.create_time, cp.use_start_time, cp.use_end_time, cp.use_time, cp.use_status,c.store_id,c.encrypted_code,cp.code from ( select
from customer_coupon where customer_id = #{customerId,jdbcType=VARCHAR}
and use_status = #{useStatus,jdbcType=TINYINT} and use_end_time > now()
and use_status = #{useStatus,jdbcType=TINYINT}
and use_end_time <= now() and use_status != 1
) cp inner join ( select id ,code,encrypted_code,store_id from coupon
where store_id = #{storeId,jdbcType=TINYINT}
) c on c.code=cp.coupon_code
order by cp.create_time desc

select coupon_code as couponCode,count(1) as recievedCount
from customer_coupon
where coupon_code in

  #{couponCode}


  and customer_id = #{customerId,jdbcType=VARCHAR}

group BY coupon_code
