mapper.select:
select date_format(estimated_arrive_time, '%H:%i') as dayString
from srv_reservation_order
where date_format(estimated_arrive_time, '%Y-%m-%d') = #{dateString}
      and store_id=#{storeId}
      and status in('CONFIRMED','UNCONFIRMED')
      and customer_id=#{customerId}

mapper.update:
update srv_reservation_order
set estimated_arrive_time = #{estimatedArriveTime},customer_phone_number = #{customerPhoneNumber},customer_id = #{customerId},
description = #{description},status='UNCONFIRMED',update_time = now(),update_user = #{updateUser}
where id = #{id}

mapper.select:
select * from srv_reservation_order
where store_id = #{storeId}
and customer_id = #{customerId}
ORDER BY estimated_arrive_time DESC
limit ${pageIndex*pageSize},${pageSize}

select count(1) from srv_reservation_order
where store_id = #{storeId}
and customer_id = #{customerId}

select * from srv_reservation_order
where store_id = #{storeId}
and date_format(estimated_arrive_time, '%Y-%m-%d') = #{reservationDate}
order by status asc,create_time desc

select count(1) from srv_reservation_order
where store_id = #{storeId}
and tenant_id = #{tenantId}
and status in ('UNCONFIRMED','CONFIRMED')
